SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_especialidade_med_prof ( cd_especialidade_p text, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_especialidade_w 	varchar(100);
ie_somente_prof_W	varchar(1);


BEGIN

ds_especialidade_w 	:= null;
ie_somente_prof_w 	:= obter_valor_param_usuario(281,745, obter_perfil_ativo, nm_usuario_p, 0);

if (ie_somente_prof_w = 'N') then
	SELECT 	m.ds_especialidade
	INTO STRICT	ds_especialidade_w
	FROM	especialidade_medica m
	WHERE (m.cd_especialidade      = cd_especialidade_p)
	AND (m.ie_situacao = 'A');
else
	SELECT 	p.ds_especialidade_prof
	INTO STRICT	ds_especialidade_w
	FROM	especialidade_profissional p
	WHERE (p.cd_especialidade_prof = cd_especialidade_p)
	AND (p.ie_situacao = 'A');
end if;

return	ds_especialidade_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
