CREATE OR REPLACE FUNCTION obter_especie_nota_fiscal ( nr_seq_nota_p bigint) RETURNS varchar AS $body$
DECLARE


sg_especie_w	varchar(255);


BEGIN

select    coalesce(m.sg_modelo, 'NF')
into STRICT	  sg_especie_w
FROM nota_fiscal a
LEFT OUTER JOIN modelo_nota_fiscal m ON (a.nr_seq_modelo = m.nr_sequencia)
WHERE a.nr_sequencia = nr_seq_nota_p;



RETURN sg_especie_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
