SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_espec_agecons ( nr_seq_agecons_p bigint) RETURNS varchar AS $body$
DECLARE

 
cd_agenda_w		bigint;
cd_especialidade_w	integer;
ds_especialidade_w	varchar(100);


BEGIN 
 
if (nr_seq_agecons_p IS NOT NULL AND nr_seq_agecons_p::text <> '') then 
	begin 
 
	select	coalesce(max(cd_agenda),0) 
	into STRICT	cd_agenda_w 
	from	agenda_consulta 
	where	nr_sequencia	= nr_seq_agecons_p;
	 
	select	coalesce(max(cd_especialidade),0) 
	into STRICT	cd_especialidade_w 
	from	agenda 
	where	cd_agenda		= cd_agenda_w;
	 
	ds_especialidade_w	:= substr(Obter_Nome_Especialidade(cd_especialidade_w),1,100);	
	end;
end if;
 
return ds_especialidade_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
