CREATE OR REPLACE FUNCTION obter_espec_atend_futuro ( cd_pessoa_fisica_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* IE_OPCAO_P
D - Descrição
*/
ds_retorno_w		varchar(2000);



BEGIN

select	max(substr(OBTER_DESC_ESPEC_MEDICA(cd_especialidade),1,90))
into STRICT	ds_retorno_w
from   	atend_pac_futuro a
where	coalesce(a.nr_atendimento::text, '') = ''
and    	a.cd_pessoa_fisica = cd_pessoa_fisica_p
order by 1;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
