CREATE OR REPLACE FUNCTION obter_espec_medico_agenda ( cd_pessoa_fisica_p text, cd_agenda_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w			varchar(4000);
cd_especialidade_w		bigint;
					

BEGIN 
 
begin 
select	cd_especialidade 
into STRICT	cd_especialidade_w 
from	agenda 
where	cd_pessoa_fisica	= cd_pessoa_fisica_p 
and	cd_agenda		= cd_agenda_p 
and	(cd_especialidade IS NOT NULL AND cd_especialidade::text <> '');
exception 
	when others then 
	cd_especialidade_w	:= null;
end;	
 
if (coalesce(cd_especialidade_w::text, '') = '') then 
	ds_retorno_w	:= obter_especialidades_medico(cd_pessoa_fisica_p);
else	 
	ds_retorno_w	:= obter_desc_espec_medica(cd_especialidade_w);
end if;
 
return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
