CREATE OR REPLACE FUNCTION obter_espec_medico_atend (nr_atendimento_p bigint, cd_medico_p text, ie_cod_desc_p text) RETURNS varchar AS $body$
DECLARE


cd_especialidade_w		integer	:= null;
ds_especialidade_w		varchar(255)	:= '';

C01 CURSOR FOR
SELECT	cd_especialidade
from	atendimento_troca_medico
where	cd_medico_atual = cd_medico_p
and 	nr_atendimento = nr_atendimento_p
order by nr_sequencia;


BEGIN

OPEN C01;
LOOP
FETCH C01 into
	cd_especialidade_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	cd_especialidade_w	:= cd_especialidade_w;
END LOOP;
CLOSE C01;

if (cd_especialidade_w IS NOT NULL AND cd_especialidade_w::text <> '') and (ie_cod_desc_p = 'D') then
	select	ds_especialidade
	into STRICT	ds_especialidade_w
	from	especialidade_medica
	where	cd_especialidade = cd_especialidade_w;
end if;

if (substr(ie_cod_desc_p,1,1) <> 'D') then
	ds_especialidade_w	:= cd_especialidade_w;
end if;

RETURN ds_especialidade_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
