CREATE OR REPLACE FUNCTION obter_espec_medic_resp_eup ( nr_atendimento_p bigint, ie_opcao_p text default null) RETURNS varchar AS $body$
DECLARE

				
/*	ie_opcao_p (Padrao descricao)
D = Retorna a descricao da especialidade do medico
C = Retorna o codigo da especialidade do medico
*/
				
ds_especialidade_w varchar(100);
cd_especialidade_w bigint;

BEGIN

SELECT 	max(CD_ESPEC_MEDICO_RESP),
		substr(OBTER_DESC_ESPEC_MEDICA(max(CD_ESPEC_MEDICO_RESP)),1,100)
into STRICT 	cd_especialidade_w,
		ds_especialidade_w
FROM 	ATEND_PACIENTE_ADIC a
WHERE 	a.NR_ATENDIMENTO = nr_atendimento_p;

if (coalesce(ie_opcao_p,'D') = 'C') then
	return	cd_especialidade_w;
else
	return	ds_especialidade_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
