SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_espec_proc_regra (cd_procedimento_p bigint, ie_origem_proced_p bigint) RETURNS varchar AS $body$
DECLARE


ds_especialidade_w 			varchar(255);
cd_area_procedimento_w			bigint;
cd_especialidade_w			bigint;
cd_grupo_proc_w				bigint;
nr_seq_forma_org_w             		bigint;
nr_seq_grupo_w				bigint;
nr_seq_subgrupo_w              		bigint;

c01 CURSOR FOR
	SELECT	b.ds_especialidade
	from	proced_especialidade a,
		especialidade_medica b
	where	a.cd_especialidade 	= b.cd_especialidade
	and	coalesce(a.cd_area_procedimento, coalesce(cd_area_procedimento_w,0))		= coalesce(cd_area_procedimento_w,0)
	and	coalesce(a.cd_especialidade_proc, coalesce(cd_especialidade_w,0))			= coalesce(cd_especialidade_w,0)
	and	coalesce(a.cd_grupo_proc, coalesce(cd_grupo_proc_w,0))				= coalesce(cd_grupo_proc_w,0)
	and	coalesce(a.nr_seq_forma_org, coalesce(nr_seq_forma_org_w,0))			= coalesce(nr_seq_forma_org_w,0)
	and	coalesce(a.nr_seq_grupo, coalesce(nr_seq_grupo_w,0))				= coalesce(nr_seq_grupo_w,0)
	and	coalesce(a.nr_seq_subgrupo, coalesce(nr_seq_subgrupo_w,0))			= coalesce(nr_seq_subgrupo_w,0)
	and	((coalesce(a.cd_procedimento::text, '') = '') or (coalesce(a.cd_procedimento, coalesce(cd_procedimento_p,0))			= cd_procedimento_p)
		and (coalesce(a.ie_origem_proced, coalesce(ie_origem_proced_p,0))		= coalesce(ie_origem_proced_p,0)))
	order by	coalesce(a.cd_procedimento, 0),
		coalesce(a.cd_grupo_proc, 0),
		coalesce(a.cd_especialidade, 0),
		coalesce(a.cd_area_procedimento,0);


BEGIN

if (coalesce(ie_origem_proced_p,0) <> 7) then
	begin

	select	max(cd_area_procedimento),
		max(cd_especialidade),
		max(cd_grupo_proc)
	into STRICT	cd_area_procedimento_w,
		cd_especialidade_w,
		cd_grupo_proc_w
	from	estrutura_procedimento_v
	where	cd_procedimento		= cd_procedimento_p
	and	ie_origem_proced	= ie_origem_proced_p;

	open c01;
		loop
		fetch c01 into	ds_especialidade_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
			begin
			ds_especialidade_w	:= ds_especialidade_w;
			end;
		end loop;
		close c01;

	end;
else
	begin

	select	nr_seq_forma_org,
		nr_seq_grupo,
		nr_seq_subgrupo
	into STRICT	nr_seq_forma_org_w,
		nr_seq_grupo_w,
		nr_seq_subgrupo_w
	from	sus_estrutura_procedimento_v
	where	cd_procedimento		= cd_procedimento_p
	and	ie_origem_proced	= ie_origem_proced_p;

	open c01;
		loop
		fetch c01 into	ds_especialidade_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
			begin
			ds_especialidade_w	:= ds_especialidade_w;
			end;
		end loop;
		close c01;

	end;
end if;

return	ds_especialidade_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
