SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_estabilidade_mat_prescr (nr_prescricao_p bigint, nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_estab_w		bigint;
qt_estab_w		double precision;
cd_diluente_w		bigint;
cd_diluente_prescr_w	bigint;
ie_estagio_w		varchar(10);
nr_seq_diluicao_w	bigint;
nr_seq_reconstituicao_w	bigint;
cd_material_w		bigint;
cd_reconstituinte_w	bigint;
cd_estabelecimento_w	bigint;
ie_via_aplicacao_w	varchar(5);
qt_idade_w		bigint;
qt_peso_w		double precision;
ie_tempo_estab_w	varchar(5);
qt_estabilidade_w	double precision := null;

c01 CURSOR FOR
SELECT	a.ie_tempo_estab,
	a.qt_estabilidade
FROM mat_estagio_armaz x, material_armazenamento a
LEFT OUTER JOIN material_diluicao c ON (a.NR_SEQ_RECONSTITUICAO = c.nr_seq_interno)
LEFT OUTER JOIN material_diluicao b ON (a.NR_SEQ_DILUICAO = b.nr_seq_interno)
WHERE x.nr_sequencia		= a.nr_seq_estagio and a.cd_material		= cd_material_w and coalesce(a.cd_estabelecimento,coalesce(cd_estabelecimento_w,0)) = coalesce(cd_estabelecimento_w,0) and ((coalesce(a.ie_via_aplicacao::text, '') = '') or (a.ie_via_aplicacao	= ie_via_aplicacao_w)) and ((coalesce(NR_SEQ_RECONSTITUICAO::text, '') = '') or
	 (cd_reconstituinte_w IS NOT NULL AND cd_reconstituinte_w::text <> '' AND c.cd_diluente	= cd_reconstituinte_w)) and ((coalesce(NR_SEQ_DILUICAO::text, '') = '') or
	 (cd_diluente_prescr_w IS NOT NULL AND cd_diluente_prescr_w::text <> '' AND b.cd_diluente	= cd_diluente_prescr_w)) and ((x.ie_estagio = '1' and (cd_diluente_prescr_w IS NOT NULL AND cd_diluente_prescr_w::text <> '')) or (x.ie_estagio = '2' and (cd_reconstituinte_w IS NOT NULL AND cd_reconstituinte_w::text <> '')) or (x.ie_estagio = '3')) order by replace(x.ie_estagio,'3','0') desc,
		 a.nr_seq_apres,
		 a.ie_tempo_estab;


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	select	max(cd_estabelecimento)
	into STRICT	cd_estabelecimento_w
	from	prescr_medica
	where	nr_prescricao	= nr_prescricao_p;

	select	a.ie_via_aplicacao,
		a.cd_material
	into STRICT	ie_via_aplicacao_w,
		cd_material_w
	from	prescr_material a
	where	nr_prescricao	= nr_prescricao_p
	and	nr_sequencia	= nr_sequencia_p;

	select	max(a.cd_material)
	into STRICT	cd_diluente_prescr_w
	from	prescr_material a
	where	a.nr_sequencia_diluicao	= nr_sequencia_p
	and	a.nr_prescricao		= nr_prescricao_p
	and	a.ie_agrupador		= 3;

	select	max(a.cd_material)
	into STRICT	cd_reconstituinte_w
	from	prescr_material a
	where	a.nr_sequencia_diluicao	= nr_sequencia_p
	and	a.nr_prescricao		= nr_prescricao_p
	and	a.ie_agrupador		= 9;

	qt_estabilidade_w := null;
	qt_estab_w	:= null;
	ie_tempo_estab_w := null;

	open c01;
	loop
	fetch c01 into
		ie_tempo_estab_w,
		qt_estabilidade_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		ie_tempo_estab_w	:= ie_tempo_estab_w;
		qt_estabilidade_w	:= qt_estabilidade_w;

	end loop;
	close c01;

	if (ie_tempo_estab_w IS NOT NULL AND ie_tempo_estab_w::text <> '') and (qt_estabilidade_w IS NOT NULL AND qt_estabilidade_w::text <> '') then
		qt_estab_w := converte_estab_hor(ie_tempo_estab_w, qt_estabilidade_w);
	else
		qt_estab_w := null;
	end if;

end if;

return qt_estab_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
