CREATE OR REPLACE FUNCTION obter_estab_agenda_gv ( nr_seq_agenda_p bigint, cd_tipo_agenda_p bigint) RETURNS varchar AS $body$
DECLARE


ds_estab_w	varchar(255);


BEGIN
if (coalesce(nr_seq_agenda_p,0) > 0) then
	if (cd_tipo_agenda_p in (1,2)) then

		begin
			select	substr(OBTER_NOME_ESTABELECIMENTO(OBTER_ESTAB_AGENDA(cd_agenda)),1,255)
			into STRICT	ds_estab_w
			from	agenda_paciente
			where	nr_sequencia = nr_seq_agenda_p;
		exception
		when others then
			ds_estab_w := '';
		end;

	elsif (cd_tipo_agenda_p in (3,4,5)) then

		begin
			select	substr(OBTER_NOME_ESTABELECIMENTO(OBTER_ESTAB_AGENDA(cd_agenda)),1,255)
			into STRICT	ds_estab_w
			from	agenda_consulta
			where	nr_sequencia = nr_seq_agenda_p;
		exception
		when others then
			ds_estab_w := '';
		end;


	end if;
end if;
return	ds_estab_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
