CREATE OR REPLACE FUNCTION obter_estab_cr (cd_estab_origem_p bigint, ie_nota_credito_p text) RETURNS bigint AS $body$
DECLARE


cd_estabelecimento_w	smallint;


BEGIN

select	coalesce(max(a.cd_estab_destino),cd_estab_origem_p)
into STRICT	cd_estabelecimento_w
from	regra_estab_cr a
where	a.cd_estab_origem	= cd_estab_origem_p
and	coalesce(a.ie_nota_credito,'N') = ie_nota_credito_p;

return coalesce(cd_estabelecimento_w,cd_estab_origem_p);

end;
