CREATE OR REPLACE FUNCTION obter_estab_movto_trans_financ (nr_seq_caixa_p bigint, nr_seq_banco_p bigint) RETURNS bigint AS $body$
DECLARE


cd_estabelecimento_w	integer;


BEGIN

if (nr_seq_caixa_p IS NOT NULL AND nr_seq_caixa_p::text <> '') then
	begin
	select	cd_estabelecimento
	into STRICT	cd_estabelecimento_w
	from	caixa
	where	nr_sequencia	= nr_seq_caixa_p;
	exception when no_data_found then
		cd_estabelecimento_w := null;
	end;
else
	begin
	select	cd_estabelecimento
	into STRICT	cd_estabelecimento_w
	from	banco_estabelecimento
	where	nr_sequencia	= nr_seq_banco_p;
	exception when no_data_found then
		cd_estabelecimento_w := null;
	end;	
	
	if (coalesce(cd_estabelecimento_w::text, '') = '') then -- AAMFIRMO OS 666792 Se nao tiver estabelecimento cadastrado na conta, busca pelo estabelecimento adicional da conta.
		 select max(cd_estabelecimento)
		 into STRICT	cd_estabelecimento_w
		 from	BANCO_ESTAB_ESTAB
		 where	nr_seq_conta_banco	= nr_seq_banco_p;
	end if;
end if;

return	cd_estabelecimento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
