CREATE OR REPLACE FUNCTION obter_estab_processo_aprov ( nr_seq_processo_p bigint) RETURNS bigint AS $body$
DECLARE



cd_estabelecimento_w		estabelecimento.cd_estabelecimento%type;


BEGIN

select	coalesce(max(cd_estabelecimento), 0)
into STRICT	cd_estabelecimento_w
from	ordem_compra b,
	ordem_compra_item a
where	a.nr_ordem_compra		= b.nr_ordem_compra
and	a.nr_seq_aprovacao	= nr_seq_processo_p;

if (cd_estabelecimento_w = 0) then
	select	coalesce(max(b.cd_estabelecimento),0)
	into STRICT	cd_estabelecimento_w
	from	solic_compra b,
		solic_compra_item a
	where	a.nr_solic_compra		= b.nr_solic_compra
	and	a.nr_seq_aprovacao	= nr_seq_processo_p;
end if;

if (cd_estabelecimento_w = 0) then
	select	coalesce(max(b.cd_estabelecimento),0)
	into STRICT	cd_estabelecimento_w
	from	requisicao_material b,
		item_requisicao_material a
	where	a.nr_requisicao		= b.nr_requisicao
	and	a.nr_seq_aprovacao	= nr_seq_processo_p;
end if;

if (cd_estabelecimento_w = 0) then
	select	coalesce(max(b.cd_estabelecimento),0)
	into STRICT	cd_estabelecimento_w
	from	cot_compra b,
		cot_compra_item a
	where	a.nr_cot_compra		= b.nr_cot_compra
	and	a.nr_seq_aprovacao	= nr_seq_processo_p;
end if;

if (cd_estabelecimento_w = 0) then
	select	coalesce(max(b.cd_estabelecimento),0)
	into STRICT	cd_estabelecimento_w
	from	nota_fiscal b,
		nota_fiscal_item a
	where	a.nr_sequencia		= b.nr_sequencia
	and	a.nr_seq_aprovacao	= nr_seq_processo_p;
end if;

if (cd_estabelecimento_w = 0) then
	select	coalesce(max(a.cd_estabelecimento),0)
	into STRICT	cd_estabelecimento_w
	from	reg_licitacao a,
		reg_lic_item b
	where	a.nr_sequencia		= b.nr_seq_licitacao
	and	b.nr_seq_aprovacao	= nr_seq_processo_p;
end if;

if (cd_estabelecimento_w = 0) then
	select	coalesce(max(a.cd_estabelecimento),0)
	into STRICT	cd_estabelecimento_w
	from	contrato a
	where	a.nr_seq_aprovacao	= nr_seq_processo_p;
end if;

if (cd_estabelecimento_w = 0) then
	select	coalesce(max(a.cd_estabelecimento),0)
	into STRICT	cd_estabelecimento_w
	from	projeto_recurso a
	where	a.nr_seq_aprovacao	= nr_seq_processo_p;
end if;

return cd_estabelecimento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
