CREATE OR REPLACE FUNCTION obter_estab_proc_mat_repasse (nr_seq_proc_repasse_p bigint, nr_seq_mat_repasse_p bigint) RETURNS bigint AS $body$
DECLARE

 
cd_estabelecimento_w	bigint;


BEGIN 
 
if (coalesce(nr_seq_proc_repasse_p,0) > 0) then 
	select	c.cd_estabelecimento 
	into STRICT	cd_estabelecimento_w 
	from	conta_paciente c, 
		procedimento_paciente b, 
		procedimento_repasse a 
	where	a.nr_sequencia		= nr_seq_proc_repasse_p 
	and	a.nr_seq_procedimento	= b.nr_sequencia 
	and	b.nr_interno_conta		= c.nr_interno_conta;
elsif (coalesce(nr_seq_mat_repasse_p,0) > 0) then 
	select	c.cd_estabelecimento 
	into STRICT	cd_estabelecimento_w 
	from	conta_paciente c, 
		material_atend_paciente b, 
		material_repasse a 
	where	a.nr_sequencia		= nr_seq_mat_repasse_p 
	and	a.nr_seq_material		= b.nr_sequencia 
	and	b.nr_interno_conta		= c.nr_interno_conta;
end if;
 
return	cd_estabelecimento_w;
 
end;
