CREATE OR REPLACE FUNCTION obter_estab_prontuario_lote ( nr_seq_solic_p bigint) RETURNS bigint AS $body$
DECLARE



nr_atendimento_w	bigint;
cd_pessoa_fisica_w	varchar(10);
dt_periodo_inicial_w	timestamp;
dt_periodo_final_w	timestamp;
nr_seq_local_w		bigint;
cd_estabelecimento_w	smallint;


BEGIN

if (nr_seq_solic_p IS NOT NULL AND nr_seq_solic_p::text <> '') then
	select	coalesce(nr_atendimento,0),
		cd_pessoa_fisica,
		dt_periodo_inicial,
		dt_periodo_final
	into STRICT	nr_atendimento_w,
		cd_pessoa_fisica_w,
		dt_periodo_inicial_w,
		dt_periodo_final_w
	from	same_solic_pront
	where	nr_sequencia = nr_seq_solic_p;

	select	max(nr_seq_local)
	into STRICT	nr_seq_local_w
	from	same_prontuario
	where (nr_atendimento		= nr_atendimento_w or nr_atendimento_w = 0)
	and	cd_pessoa_fisica	= coalesce(cd_pessoa_fisica_w, cd_pessoa_fisica)
	and (dt_periodo_inicial_w	>= dt_periodo_inicial or coalesce(dt_periodo_inicial_w::text, '') = '')
	and (dt_periodo_final_w	<= coalesce(dt_periodo_final,clock_timestamp()));

	if (nr_seq_local_w IS NOT NULL AND nr_seq_local_w::text <> '') then
		select	coalesce(max(cd_estabelecimento),0)
		into STRICT	cd_estabelecimento_w
		from	same_local
		where 	nr_sequencia = nr_seq_local_w;
	else
		cd_estabelecimento_w := 0;
	end if;

end if;

return	cd_estabelecimento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
