CREATE OR REPLACE FUNCTION obter_estab_tipo_episodio ( nr_seq_tipo_episodio_p bigint, cd_estabelecimento_p text) RETURNS varchar AS $body$
DECLARE



nr_seq_tipo_episodio_w  tipo_episodio_estab.nr_seq_tipo_episodio%type;
cd_estabelecimento_w    tipo_episodio_estab.cd_estabelecimento%type;
ds_retorno_w			varchar(1);
vl_retorno_w        	tipo_episodio_estab.nr_sequencia%type := null;


BEGIN

nr_seq_tipo_episodio_w  :=  nr_seq_tipo_episodio_p;
cd_estabelecimento_w    :=  cd_estabelecimento_p;

if (coalesce(cd_estabelecimento_w::text, '') = '') then
    cd_estabelecimento_w    :=  to_char(wheb_usuario_pck.get_cd_estabelecimento);
end if;

    select count(*)
    into STRICT	vl_retorno_w
    from	tipo_episodio_estab
    where	nr_seq_tipo_episodio = nr_seq_tipo_episodio_w;

if (vl_retorno_w > 0) and (vl_retorno_w IS NOT NULL AND vl_retorno_w::text <> '') then

    select count(*)
    into STRICT	vl_retorno_w
    from	tipo_episodio_estab
    where	nr_seq_tipo_episodio = nr_seq_tipo_episodio_w
	and cd_estabelecimento = cd_estabelecimento_w;

else

    vl_retorno_w    :=  1;

end if;

if (vl_retorno_w > 0) and (vl_retorno_w IS NOT NULL AND vl_retorno_w::text <> '') then

    ds_retorno_w := 'S';

else

    ds_retorno_w := 'N';

end if;

return	ds_retorno_w;

end;
