CREATE OR REPLACE FUNCTION obter_estab_usuario_pf ( cd_pessoa_fisica_p text) RETURNS bigint AS $body$
DECLARE


nm_usuario_w		usuario.nm_usuario%type;
cd_estabelecimento_w	usuario.cd_estabelecimento%type;


BEGIN

if (coalesce(cd_pessoa_fisica_p,'0') <> '0') then
	begin
	select	max(nm_usuario)
	into STRICT	nm_usuario_w
	from	usuario
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	ie_situacao = 'A';

	if (coalesce(nm_usuario_w,'0') <> '0') then
		begin
		select	cd_estabelecimento
		into STRICT	cd_estabelecimento_w
		from	usuario
		where	nm_usuario = nm_usuario_w;
		exception
		when others then
			cd_estabelecimento_w := null;
		end;
	end if;
	end;
end if;

return	cd_estabelecimento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
