CREATE OR REPLACE FUNCTION obter_estab_usuario (nm_usuario_p text) RETURNS bigint AS $body$
DECLARE


cd_estabelecimento_w	bigint;

BEGIN

if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	select	cd_estabelecimento
	into STRICT	cd_estabelecimento_w
	from	usuario
	where	Upper(nm_usuario) = Upper(nm_usuario_p);
end if;

return cd_estabelecimento_w;

end;
$body$
