CREATE OR REPLACE FUNCTION obter_estadio ( cd_topografia_p text, cd_tumor_primario_p text, cd_linfonodo_regional_p text, cd_metastase_distancia_p text) RETURNS varchar AS $body$
DECLARE



nr_sequencia_w			bigint;
cd_estadio_w			varchar(15);
ds_retorno_w			varchar(15);
cd_tumor_primario_w		can_estadio.cd_tumor_primario%type;
cd_linfonodo_regional_w		can_estadio.cd_linfonodo_regional%type;
cd_metastase_distancia_w	can_estadio.cd_metastase_distancia%type;


c01 CURSOR FOR
	SELECT	upper(cd_tumor_primario),
		upper(cd_linfonodo_regional),
		upper(cd_metastase_distancia),
		upper(cd_estadio)
	from	can_estadio
	where	nr_seq_loc_tnm = nr_sequencia_w
	and	((coalesce(cd_tumor_primario::text, '') = '') or (coalesce(cd_linfonodo_regional::text, '') = '') or (coalesce(cd_metastase_distancia::text, '') = ''))
	order by	
		cd_tumor_primario,
		cd_linfonodo_regional,
		cd_metastase_distancia;


BEGIN

select	a.nr_seq_loc_tnm
into STRICT	nr_sequencia_w
from	cido_topografia a
where	cd_topografia = cd_topografia_p;


if	((cd_tumor_primario_p IS NOT NULL AND cd_tumor_primario_p::text <> '') and (cd_linfonodo_regional_p IS NOT NULL AND cd_linfonodo_regional_p::text <> '') and (cd_metastase_distancia_p IS NOT NULL AND cd_metastase_distancia_p::text <> '')) then
	begin
	select	max(cd_estadio)
	into STRICT	cd_estadio_w
	from	can_estadio
	where	nr_seq_loc_tnm			= nr_sequencia_w
	and	upper(cd_tumor_primario)	= upper(cd_tumor_primario_p)
	and	upper(cd_linfonodo_regional)	= upper(cd_linfonodo_regional_p)
	and	upper(cd_metastase_distancia)	= upper(cd_metastase_distancia_p);

	exception when no_data_found then
		 cd_estadio_w := null;
	end;

	ds_retorno_w	:= cd_estadio_w;
end if;

if (coalesce(cd_estadio_w::text, '') = '') then
	begin
	open c01;
	loop
	fetch c01 into
		cd_tumor_primario_w,
		cd_linfonodo_regional_w,
		cd_metastase_distancia_w,
		cd_estadio_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	
	if (cd_tumor_primario_w = cd_tumor_primario_p) and (cd_linfonodo_regional_w = cd_linfonodo_regional_p) then
		ds_retorno_w:=	cd_estadio_w;
	elsif (cd_tumor_primario_w = cd_tumor_primario_p) and (cd_metastase_distancia_w = cd_metastase_distancia_p) then
		ds_retorno_w:=	cd_estadio_w;
	elsif (cd_linfonodo_regional_w = cd_linfonodo_regional_p) and (cd_metastase_distancia_w = cd_metastase_distancia_p) then
		ds_retorno_w:=	cd_estadio_w;
	elsif (cd_metastase_distancia_w = cd_metastase_distancia_p) and (cd_tumor_primario_w = cd_tumor_primario_p) then
		ds_retorno_w:=	cd_estadio_w;
	end if;

	if (ds_retorno_w <> '') then
		exit;
	end if;

	end;
	end loop;
	close c01;
	end;
end if;

return	ds_retorno_w;

end;
$body$
