SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_estagio_anterior_autor (nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*ie_opcao_p
I -> ie_interno do cadastro do estágio
S -> Sequência do estágio
D -> Descrição do estágio
*/
ie_interno_w		varchar(10);
ds_estagio_w		varchar(255);
ds_retorno_w		varchar(255);
nr_seq_estagio_w	bigint;


BEGIN

select	max(a.nr_seq_estagio)
into STRICT	nr_seq_estagio_w
from	autorizacao_convenio_hist a
where	a.nr_sequencia_autor	= nr_sequencia_p
and	a.dt_atualizacao	in (SELECT	max(b.dt_atualizacao)
				from	autorizacao_convenio_hist b,
					autorizacao_convenio c
				where	b.nr_sequencia_autor	= c.nr_sequencia
				and	c.nr_sequencia		= nr_sequencia_p
				and	c.nr_seq_estagio	<> b.nr_seq_estagio
				and	(b.nr_seq_estagio IS NOT NULL AND b.nr_seq_estagio::text <> ''));

if (nr_seq_estagio_w IS NOT NULL AND nr_seq_estagio_w::text <> '') then

	select	substr(ds_estagio,1,255),
		ie_interno
	into STRICT	ds_estagio_w,
		ie_interno_w
	from	estagio_autorizacao
	where	nr_sequencia	= nr_seq_estagio_w
	and	OBTER_EMPRESA_ESTAB(wheb_usuario_pck.get_cd_estabelecimento) = cd_empresa;

end if;

if (ie_opcao_p = 'I') then
	ds_retorno_w	:= ie_interno_w;
elsif (ie_opcao_p = 'S') then
	ds_retorno_w	:= nr_seq_estagio_w;
elsif (ie_opcao_p = 'D') then
	ds_retorno_w	:= ds_estagio_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
