CREATE OR REPLACE FUNCTION obter_estagio_atual_opm (nr_seq_opm_p bigint ) RETURNS varchar AS $body$
DECLARE


c01 CURSOR FOR
	SELECT	ie_estagio_atual
	from 		prescr_opm_historico
	where 	nr_seq_opm = nr_seq_opm_p
	order by dt_atualizacao;

ie_estagio_atual_w	varchar(15);
ie_estagio_atual_ww  varchar(15);

BEGIN

open c01;
loop
fetch c01 into
	ie_estagio_atual_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	ie_estagio_atual_ww := ie_estagio_atual_w;
	end;
end loop;
close C01;

return ie_estagio_atual_ww;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
