    ** Autor: Maxwell da Silva Oliveira 
    ** Data: 01/10/2019       
    ********************************************
    */
    ds_estagio_w varchar(60);

BEGIN
    SELECT MAX(b.ds_estagio)
      INTO STRICT ds_estagio_w
      FROM estagio_autorizacao  b,
           autorizacao_convenio a,
           agenda_consulta      x
     WHERE a.nr_seq_estagio = b.nr_sequencia
       AND a.nr_seq_agenda_consulta = x.nr_seq_agenda_sessao
       AND x.nr_sequencia = nr_seq_agenda_p;
    RETURN ds_estagio_w;
END;
