CREATE OR REPLACE FUNCTION obter_estagio_autor_ageexa ( nr_seq_agenda_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, nr_seq_proc_interno_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(4000)	:= null;
nr_sequencia_autor_w	procedimento_autorizado.nr_sequencia_autor%Type;
nr_seq_estagio_w	estagio_autorizacao.nr_sequencia%Type;
ds_estagio_w		estagio_autorizacao.ds_estagio%Type;
ds_estagio_autor_w	varchar(255);


BEGIN

if (coalesce(nr_seq_agenda_p,0) > 0) then

	select	max(p.nr_sequencia_autor)
	into STRICT	nr_sequencia_autor_w
	from	procedimento_autorizado p
	where	p.nr_seq_agenda		= nr_seq_agenda_p
	and	((cd_procedimento	= cd_procedimento_p
	and	ie_origem_proced	= ie_origem_proced_p)
		or (nr_seq_proc_interno_p	= nr_seq_proc_interno_p));

	if (coalesce(nr_sequencia_autor_w, 0) = 0) then
		select	max(nr_sequencia)
		into STRICT	nr_sequencia_autor_w
		from	autorizacao_convenio
		where	nr_seq_agenda	= nr_seq_agenda_p;
	end if;

	if (coalesce(nr_sequencia_autor_w, 0) > 0) then

		select	max(b.nr_sequencia),
			substr(max(b.ds_estagio),1,255),
		 	max(substr(obter_valor_dominio(1227,b.ie_autor_agenda),1,240))
		into STRICT	nr_seq_estagio_w,
			ds_estagio_w,
			ds_estagio_autor_w
		from	estagio_autorizacao b,
			autorizacao_convenio a
		where	a.nr_seq_estagio	= b.nr_sequencia
		and	a.nr_sequencia		= nr_sequencia_autor_w;

		if (ie_opcao_p	= 'C') then -- Sequência do Estágio da autorização
			ds_retorno_w	:= nr_seq_estagio_w;
		elsif (ie_opcao_p	= 'D') then -- Descrição do Estágio da autorização
			ds_retorno_w	:= substr(ds_estagio_w,1,255);
		elsif (ie_opcao_p	= 'DE') then
			ds_retorno_w	:= ds_estagio_autor_w;
		end if;
	end if;

end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
