CREATE OR REPLACE FUNCTION obter_estagio_autor_agenda ( nr_seq_agenda_p bigint ) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(15) 	:= null;
qt_neces_autor_w		bigint	:= 0;
qt_neces_negada_w	bigint	:= 0;
qt_neces_penden_w	bigint	:= 0;
cd_estabelecimento_w	integer;
ie_estagio_parcial_w	varchar(15) 	:= 'N';


BEGIN

begin
select	max(b.cd_estabelecimento)
into STRICT	cd_estabelecimento_w
from	agenda b,
	agenda_paciente a
where	a.cd_agenda	= b.cd_agenda
and	a.nr_sequencia	= nr_seq_agenda_p;
exception
when others then
	cd_estabelecimento_w := wheb_usuario_pck.get_cd_estabelecimento;
end;

select	coalesce(max(ie_estagio_parc_autor_agenda),'N')
into STRICT	ie_estagio_parcial_w
from	parametro_faturamento
where	cd_estabelecimento = cd_estabelecimento_w;

if (ie_estagio_parcial_w	= 'S') then
	begin

	select	count(*)
	into STRICT	qt_neces_autor_w
	from	autorizacao_convenio a,
		estagio_autorizacao b
	where	a.nr_seq_agenda		= nr_seq_agenda_p
	and	a.nr_seq_estagio	= b.nr_sequencia
	and	b.ie_interno		= '10';

	select	count(*)
	into STRICT	qt_neces_negada_w
	from	autorizacao_convenio a,
		estagio_autorizacao b
	where	a.nr_seq_agenda		= nr_seq_agenda_p
	and	a.nr_seq_estagio	= b.nr_sequencia
	and	b.ie_interno		= '90';

	select	count(*)
	into STRICT	qt_neces_penden_w
	from	autorizacao_convenio a,
		estagio_autorizacao b
	where	a.nr_seq_agenda		= nr_seq_agenda_p
	and	a.nr_seq_estagio	= b.nr_sequencia
	and	b.ie_interno 		not in ('90','10','70');

	if (qt_neces_autor_w	> 0) and (qt_neces_negada_w	= 0) and (qt_neces_penden_w	> 0) then
		ds_retorno_w	:= 'PZ';
	elsif (qt_neces_autor_w	= 0) and (qt_neces_negada_w	> 0) and (qt_neces_penden_w	> 0) then
		ds_retorno_w	:= 'PN';
	elsif (qt_neces_autor_w 	> 0) and (qt_neces_penden_w	= 0) and (qt_neces_negada_w	= 0) then
		ds_retorno_w	:= 'A';
	elsif (qt_neces_autor_w	> 0) and (qt_neces_negada_w	> 0) then
		ds_retorno_w	:= 'P';
	else
		ds_retorno_w	:= null;
	end if;

	end;
else
	ds_retorno_w 	:= null;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_estagio_autor_agenda ( nr_seq_agenda_p bigint ) FROM PUBLIC;

