CREATE OR REPLACE FUNCTION obter_estagio_autor_atend ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


qt_autor_pend_w		integer;
qt_autor_just_w		integer;
qt_autor_enca_w		integer;
qt_autor_pend_canc_w		integer;
qt_autor_repr_w		integer;
qt_autor_canc_w		integer;
ds_retorno_w		varchar(255)	:='';
qt_autorizacao_w	integer;


BEGIN
select	count(*)
into STRICT	qt_autorizacao_w
from	autorizacao_convenio
where	nr_atendimento	= nr_atendimento_p;

if (qt_autorizacao_w 	> 0) then

	select	count(*)
	into STRICT	qt_autor_pend_w
	from	autorizacao_convenio
	where	nr_atendimento	= nr_atendimento_p
	and		obter_estagio_autor(nr_seq_estagio, 'C') in (1,2);

	if (qt_autor_pend_w > 0) then
		ds_retorno_w	:= Wheb_mensagem_pck.get_texto(309527); --'Pendência de autorização';
		goto Final;
	end if;

	select	count(*)
	into STRICT	qt_autor_just_w
	from	autorizacao_convenio
	where	nr_atendimento	= nr_atendimento_p
	and		obter_estagio_autor(nr_seq_estagio, 'C') in (3);

	if (qt_autor_just_w > 0) then
		ds_retorno_w	:= Wheb_mensagem_pck.get_texto(309530); --'Justificado';
		goto Final;
	end if;

	select	count(*)
	into STRICT	qt_autor_enca_w
	from	autorizacao_convenio
	where	nr_atendimento	= nr_atendimento_p
	and		obter_estagio_autor(nr_seq_estagio, 'C') in (5);

	if (qt_autor_enca_w > 0) then
		ds_retorno_w	:= Wheb_mensagem_pck.get_texto(309532); --'Encaminhado';
		goto Final;
	end if;

	select	count(*)
	into STRICT	qt_autor_pend_canc_w
	from	autorizacao_convenio
	where	nr_atendimento	= nr_atendimento_p
	and		obter_estagio_autor(nr_seq_estagio, 'C') in (60);

	if (qt_autor_pend_canc_w > 0) then
		ds_retorno_w	:= Wheb_mensagem_pck.get_texto(309533); --'Pendência de cancelamento';
		goto Final;
	end if;

	select	count(*)
	into STRICT	qt_autor_repr_w
	from	autorizacao_convenio
	where	nr_atendimento	= nr_atendimento_p
	and		obter_estagio_autor(nr_seq_estagio, 'C') in (90);

	if (qt_autor_repr_w > 0) then
		ds_retorno_w	:= Wheb_mensagem_pck.get_texto(309535); --'Reprovado';
		goto Final;
	end if;

	select	count(*)
	into STRICT	qt_autor_canc_w
	from	autorizacao_convenio
	where	nr_atendimento	= nr_atendimento_p
	and		obter_estagio_autor(nr_seq_estagio, 'C') in (70);

	if (qt_autor_canc_w > 0) then
		ds_retorno_w	:= Wheb_mensagem_pck.get_texto(309536); --'Cancelado';
	else
		ds_retorno_w	:= Wheb_mensagem_pck.get_texto(309537); --'Autorizado';
	end if;
end if;

<<Final>>
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
