CREATE OR REPLACE FUNCTION obter_estagio_autor_ge ( nr_prescricao_p bigint, nr_seq_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255) :='';
nr_sequencia_w		bigint;


BEGIN

SELECT	MAX(a.nr_sequencia)
INTO STRICT	nr_sequencia_w
FROM autorizacao_convenio a
LEFT OUTER JOIN procedimento_autorizado b ON (a.nr_sequencia = b.nr_sequencia_autor)
WHERE b.nr_prescricao 		= nr_prescricao_p and b.nr_seq_prescricao		= nr_seq_prescricao_p;

IF (nr_sequencia_w > 0) THEN
	SELECT	MAX(SUBSTR(obter_estagio_autor(nr_seq_estagio, 'D'), 1, 255))
	INTO STRICT	ds_retorno_w
	FROM	autorizacao_convenio
	WHERE	nr_prescricao = nr_prescricao_p
	AND		nr_sequencia 	= nr_sequencia_w;
ELSE
	ds_retorno_w := NULL;
END IF;

RETURN	ds_retorno_w;

END;
$body$
