SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_estagio_autor_item_conta (nr_sequencia_item_p bigint, ie_proc_mat_p bigint) RETURNS varchar AS $body$
DECLARE

nr_seq_item_autor_w		bigint;
ds_estagio_w		varchar(255);


BEGIN

if (ie_proc_mat_p = 1) then

	select	nr_seq_proc_autor
	into STRICT	nr_seq_item_autor_w
	from	procedimento_paciente
	where	nr_sequencia = nr_sequencia_item_p;

	if (coalesce(nr_seq_item_autor_w,0) > 0) then
		begin
		select	a.ds_estagio
		into STRICT	ds_estagio_w
		from	estagio_autorizacao a,
			autorizacao_convenio b,
			procedimento_autorizado c
		where	a.nr_sequencia = b.nr_seq_estagio
		and	b.nr_sequencia = c.nr_sequencia_autor
		and	c.nr_sequencia = nr_seq_item_autor_w;
		exception
		when others then
			ds_estagio_w := null;
		end;
	end if;

elsif (ie_proc_mat_p = 2) then

	select	nr_seq_mat_autor
	into STRICT	nr_seq_item_autor_w
	from	material_atend_paciente
	where	nr_sequencia = nr_sequencia_item_p;

	if (coalesce(nr_seq_item_autor_w,0) > 0) then
		begin
		select	a.ds_estagio
		into STRICT	ds_estagio_w
		from	estagio_autorizacao a,
			autorizacao_convenio b,
			material_autorizado c
		where	a.nr_sequencia = b.nr_seq_estagio
		and	b.nr_sequencia = c.nr_sequencia_autor
		and	c.nr_sequencia = nr_seq_item_autor_w;
		exception
		when others then
			ds_estagio_w := null;
		end;
	end if;


end if;

return	ds_estagio_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
