SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_estagio_autor ( nr_seq_estagio_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ie_estagio_w	varchar(50);
cd_empresa_w	estagio_autorizacao.cd_empresa%type;


BEGIN

cd_empresa_w := obter_empresa_estab(wheb_usuario_pck.get_cd_estabelecimento);

select	max(ie_interno)
into STRICT	ie_estagio_w
from	estagio_autorizacao
where	nr_sequencia	= nr_seq_estagio_p
and	cd_empresa 	= coalesce(cd_empresa_w,cd_empresa);

if (ie_opcao_p	= 'C') then
	return	ie_estagio_w;
elsif (ie_opcao_p	= 'D') then
	return	substr(obter_valor_dominio(1378, ie_estagio_w),1,254);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
