CREATE OR REPLACE FUNCTION obter_estagio_conta_pac ( nr_interno_conta_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
nr_seq_estagio_conta_w	bigint;
ds_estagio_w		varchar(255);
ds_retorno_w		varchar(255);

/*ie_opcao_p 
C - Código 
D - Descrição 
CF - Cor fonte 
CB - Cor backgroud 
*/
 
 

BEGIN 
 
select	nr_seq_estagio_conta 
into STRICT	nr_seq_estagio_conta_w 
from	conta_paciente 
where	nr_interno_conta = nr_interno_conta_p;
 
select	ds_estagio 
into STRICT	ds_estagio_w 
from	estagio_conta 
where	nr_sequencia = nr_seq_estagio_conta_w;
 
if (upper(ie_opcao_p) = 'C') then 
	ds_retorno_w := nr_seq_estagio_conta_w;
elsif (upper(ie_opcao_p) = 'D') then 
	ds_retorno_w := ds_estagio_w;
end if;
 
return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
