CREATE OR REPLACE FUNCTION obter_estagio_inicial (nr_regra_p bigint, ie_status_execucao_p text) RETURNS bigint AS $body$
DECLARE


nr_estagio_entrega_w ESTAGIO_STATUS_ENTREGA.NR_SEQUENCIA%TYPE;
nr_seq_status_w STATUS_REGRA_ENTREGA.NR_SEQUENCIA%TYPE;


BEGIN
nr_seq_status_w := 0;

select NR_SEQUENCIA
into STRICT nr_seq_status_w
from status_regra_entrega
where nr_seq_regra = nr_regra_p
and   ie_status_execucao = ie_status_execucao_p;

if (nr_seq_status_w IS NOT NULL AND nr_seq_status_w::text <> '') then
  select NR_SEQUENCIA
  into STRICT nr_estagio_entrega_w
  from (  SELECT nr_sequencia
      from ESTAGIO_STATUS_ENTREGA
      where nr_seq_status = nr_seq_status_w
      order by nr_seq_ordem, nr_sequencia) alias1 LIMIT 1;

end if;

return nr_estagio_entrega_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
