CREATE OR REPLACE FUNCTION obter_estagio_leucometria ( nr_seq_estagio_p bigint, nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(80);

BEGIN

if (nr_seq_estagio_p IS NOT NULL AND nr_seq_estagio_p::text <> '') then

	SELECT	ds_estagio
	into STRICT	ds_retorno_w
	FROM	san_leucometria a,
		san_estagio_leucometria b
	WHERE	a.nr_seq_estagio = b.nr_sequencia
	AND	a.nr_seq_estagio = nr_seq_estagio_p
	and	a.nr_sequencia 	 = nr_sequencia_p;
end if;
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
