SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_estagio_prescr_agenda (nr_seq_agenda_p bigint) RETURNS varchar AS $body$
DECLARE


nr_prescricao_w		bigint := 0;
qt_consistido_w		bigint := 0;
ds_retorno_w		varchar(255);


BEGIN

begin
select	nr_prescricao
into STRICT	nr_prescricao_w
from	prescr_medica
where	nr_seq_agenda = nr_seq_agenda_p  LIMIT 1;
exception
when others then
	nr_prescricao_w := 0;
end;

if (nr_prescricao_w > 0) then
	begin
	select	1
	into STRICT	qt_consistido_w
	from	prescr_material
	where	nr_prescricao = nr_prescricao_w
	and	ie_status_cirurgia = 'CB'  LIMIT 1;
	exception
	when others then
		qt_consistido_w := 0;
	end;
end if;

if (qt_consistido_w > 0) then
	ds_retorno_w	:= Wheb_mensagem_pck.get_texto(309540); --'Consistido por barras';
elsif (nr_prescricao_w > 0) and (qt_consistido_w = 0) then
	ds_retorno_w	:= Wheb_mensagem_pck.get_texto(309542); --'Prescrito';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_estagio_prescr_agenda (nr_seq_agenda_p bigint) FROM PUBLIC;

