CREATE OR REPLACE FUNCTION obter_estagio_revisao_proc_int (nr_seq_proc_interno_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_estagio_w	estagio_revisao_proc_int.ds_estagio%type;

BEGIN

begin
select	a.ds_estagio
into STRICT	ds_estagio_w
from	estagio_revisao_proc_int a,
	proc_interno_revisao b
where	a.nr_sequencia 		= b.nr_seq_estagio
and	b.nr_seq_proc_interno   = nr_seq_proc_interno_p
and	coalesce(b.dt_liberacao::text, '') = ''  LIMIT 1;
exception
when others then
	ds_estagio_w := null;
end;


return	ds_estagio_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
