SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_estagio_transferencia (nr_seq_ordem_p transf_ordem_prod.nr_seq_ordem_prod%type) RETURNS varchar AS $body$
DECLARE

			
ct_shipping_w		bigint;
ct_transit_w		bigint;
ds_receiving_w		varchar(20);
ds_expressao_w		varchar(80);

BEGIN

	select count(*)
	into STRICT ct_shipping_w
	from transf_ordem_prod
	where nr_seq_ordem_prod = nr_seq_ordem_p;

	select count(v.nr_sequencia)
	into STRICT ct_transit_w
	from viagem_transferencia v,
		 viagem_transf_ordem o,
		 viagem_transf_destino d,
		 transf_ordem_prod t
	Where d.nr_seq_viagem = v.nr_sequencia
	and o.nr_seq_viagem = v.nr_sequencia
	and o.nr_ordem_compra = t.nr_ordem_compra
	and t.nr_seq_ordem_prod = nr_seq_ordem_p;
	
	select CASE WHEN ie_conf_recebimento='S' THEN  wheb_mensagem_pck.get_texto(1145610) WHEN ie_conf_recebimento='N' THEN  wheb_mensagem_pck.get_texto(1145611) END
	into STRICT ds_receiving_w
	from can_ordem_prod
	where nr_sequencia = nr_seq_ordem_p;

	if (length(ds_receiving_w) > 0) then
		ds_expressao_w	:= ds_receiving_w;
	elsif (ct_transit_w > 0) then
		ds_expressao_w := wheb_mensagem_pck.get_texto(1145057);
	elsif (ct_shipping_w > 0) then
		ds_expressao_w	:= wheb_mensagem_pck.get_texto(1144326);
	end if;

	return ds_expressao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
