SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_estag_autor_ageint_adic (nr_seq_adic_item_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(4000)	:= null;
nr_sequencia_autor_w	bigint;
nr_seq_estagio_w		bigint;
nr_seq_proc_interno_w	bigint;
ds_estagio_w		varchar(255);
cd_senha_w		varchar(20);
ds_observacao_w		varchar(4000);
nr_seq_agenda_exame_w	        agenda_integrada_item.nr_seq_agenda_exame%type;
nr_seq_ageint_w		bigint;


BEGIN

if (nr_seq_adic_item_p IS NOT NULL AND nr_seq_adic_item_p::text <> '') then

	select	max(nr_seq_agenda_exame),
		max(b.nr_seq_proc_interno),
		max(a.nr_seq_agenda_int)
	into STRICT	nr_seq_agenda_exame_w,
		nr_seq_proc_interno_w,
		nr_seq_ageint_w
	from	agenda_integrada_item a,
		ageint_exame_adic_item b
	where	a.nr_sequencia = b.nr_seq_item
	and	b.nr_sequencia = nr_seq_adic_item_p;
	
	if (coalesce(nr_sequencia_autor_w::text, '') = '') then
		select	max(a.nr_sequencia)
		into STRICT	nr_sequencia_autor_w
		from	autorizacao_convenio a, procedimento_autorizado b
		where	a.nr_sequencia = b.nr_sequencia_autor
		and	a.nr_seq_age_integ = nr_seq_ageint_w
		and	b.nr_seq_proc_interno = nr_seq_proc_interno_w;
	end if;
	
	if (nr_sequencia_autor_w IS NOT NULL AND nr_sequencia_autor_w::text <> '') then

		select	max(b.nr_sequencia),
			max(b.ds_estagio),
			max(a.cd_senha),
			max(a.ds_observacao)
		into STRICT	nr_seq_estagio_w,
			ds_estagio_w,
			cd_senha_w,
			ds_observacao_w
		from	estagio_autorizacao b,
			autorizacao_convenio a
		where	a.nr_seq_estagio	= b.nr_sequencia
		and	a.nr_sequencia	= nr_sequencia_autor_w;

		if (ie_opcao_p	= 'C') then
			ds_retorno_w	:= nr_seq_estagio_w;
		elsif (ie_opcao_p	= 'D') then
			ds_retorno_w	:= ds_estagio_w;
		elsif (ie_opcao_p	= 'OBS') then
			ds_retorno_w	:= ds_observacao_w;
		elsif (ie_opcao_p	= 'S') then
			ds_retorno_w	:= cd_senha_w;
		end if;
	end if;

end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
