CREATE OR REPLACE FUNCTION obter_estag_autor_ag_exame_lab (nr_seq_ageint_exame_lab_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE



ds_retorno_w		varchar(60);
ds_estagio_w		varchar(60);
nr_seq_estagio_w	bigint;
ie_interno_w		varchar(2);


BEGIN


begin
select	b.nr_sequencia,
	b.ie_interno,
	b.ds_estagio
into STRICT	nr_seq_estagio_w,
	ie_interno_w,
	ds_estagio_w
from	autorizacao_convenio a,
	estagio_autorizacao b,
	procedimento_autorizado c
where	a.nr_seq_estagio		= b.nr_sequencia
and	a.nr_sequencia			= c.nr_sequencia_autor
and	c.nr_seq_ageint_exame_lab	= nr_seq_ageint_exame_lab_p;
exception
when others then
	nr_seq_estagio_w := null;
	ie_interno_w	 := null;
	ds_estagio_w     := null;
end;


if (ie_opcao_p = 'D') and (ds_estagio_w IS NOT NULL AND ds_estagio_w::text <> '')then
	ds_retorno_w := ds_estagio_w;
end if;



return	ds_retorno_w;

end;
$body$
