CREATE OR REPLACE FUNCTION obter_estimativa_sprint ( nr_sequencia_p bigint, nr_time_p bigint) RETURNS bigint AS $body$
DECLARE


qt_carga_w		double precision;


BEGIN

select	coalesce(sum(qt_estimativa),0)
into STRICT	qt_carga_w
from	desenv_story a,
	desenv_story_sprint b
where	b.nr_sprint = nr_sequencia_p
and	a.nr_sequencia = b.nr_story
and	coalesce(b.ie_cancelado_sprint,'N') <> 'S'
and	((coalesce(nr_time_p::text, '') = '') or (nr_team = nr_time_p));

return	qt_carga_w;

end;
