SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_estipulante_reg_cobranca (nr_titulo_p bigint) RETURNS varchar AS $body$
DECLARE

 
nr_seq_pagador_w		titulo_receber.nr_seq_pagador%type;		
nm_estipulante_w		varchar(80);	
ds_retorno_w			varchar(80);	
				 

BEGIN 
 
if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then 
 
	select 	max(x.nr_seq_pagador) 
	into STRICT	nr_seq_pagador_w 
	from 	titulo_receber x 
	where 	x.nr_titulo = nr_titulo_p;
	 
	if (nr_seq_pagador_w IS NOT NULL AND nr_seq_pagador_w::text <> '') then 
		 
		select	substr(pls_obter_dados_contrato(max(d.nr_sequencia),'E'),1,80) 
		into STRICT	nm_estipulante_w 
		FROM titulo_receber b
LEFT OUTER JOIN pls_contrato_pagador c ON (b.nr_seq_pagador = c.nr_sequencia)
LEFT OUTER JOIN pls_contrato d ON (c.nr_seq_contrato = d.nr_sequencia)
WHERE c.nr_sequencia 		= nr_seq_pagador_w;
		 
		ds_retorno_w := coalesce(substr(nm_estipulante_w,1,80),'');
	 
	end if;
 
end if;
 
return	substr(coalesce(ds_retorno_w,''),1,80);
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_estipulante_reg_cobranca (nr_titulo_p bigint) FROM PUBLIC;

