CREATE OR REPLACE FUNCTION obter_estrutura_aprovacao ( nr_seq_processo_p bigint, nr_documento_p bigint) RETURNS bigint AS $body$
DECLARE


qt_nova_estrutura_w	bigint;
			

BEGIN

select	count(1)
into STRICT    qt_nova_estrutura_w
from	processo_aprov_compra a,
	processo_aprovacao b
where	a.cd_processo_aprov = b.cd_processo_aprov
and	a.nr_seq_proc_aprov = nr_seq_processo_p
and	a.nr_documento	    = nr_documento_p
and	b.ie_situacao = 'A';

return	qt_nova_estrutura_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
