CREATE OR REPLACE FUNCTION obter_estrutura_episodio () RETURNS varchar AS $body$
DECLARE


ie_estrutura_episodio_w PARAMETROS_EPISODIO.IE_UTILIZA_EPISODIO%type;
ie_estrutura_episodio_pe_est_w PARAMETROS_EPISODIO.IE_UTILIZA_EPISODIO%type;
ie_estrutura_episodio_perfil_w PARAMETROS_EPISODIO.IE_UTILIZA_EPISODIO%type;
ie_estrutura_episodio_estab_w PARAMETROS_EPISODIO.IE_UTILIZA_EPISODIO%type;


BEGIN

select max(IE_UTILIZA_EPISODIO)
into STRICT ie_estrutura_episodio_pe_est_w
from PARAMETROS_EPISODIO
where cd_perfil = obter_perfil_ativo()
and cd_estabelecimento = obter_estabelecimento_ativo();


select max(IE_UTILIZA_EPISODIO)
into STRICT ie_estrutura_episodio_perfil_w
from PARAMETROS_EPISODIO
where cd_perfil = obter_perfil_ativo();

select max(IE_UTILIZA_EPISODIO)
into STRICT ie_estrutura_episodio_estab_w
from PARAMETROS_EPISODIO
where cd_estabelecimento = obter_estabelecimento_ativo();

if (coalesce(ie_estrutura_episodio_pe_est_w, 'N') = 'S') then
  ie_estrutura_episodio_w := 'S';

elsif (coalesce(ie_estrutura_episodio_perfil_w, 'N') = 'S') then
  ie_estrutura_episodio_w := 'S';

elsif (coalesce(ie_estrutura_episodio_estab_w, 'N') = 'S') then
  ie_estrutura_episodio_w := 'S';

else
  ie_estrutura_episodio_w := 'N';
end if;

return ie_estrutura_episodio_w;

end;
