CREATE OR REPLACE FUNCTION obter_estrutura_ficha ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


cd_substancia_atc_w	varchar(10);
ds_retorno_w		varchar(4500);
ds_estrutura_w		varchar(500);

C01 CURSOR FOR
SELECT	cd_substancia_atc
from	medic_ficha_tecnica_atc
where	nr_seq_ficha_tecnica	= nr_sequencia_p
order by nr_seq_util;


BEGIN

OPEN C01;
LOOP
FETCH C01 into
	cd_substancia_atc_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	substr(Obter_estrutura_substancia(cd_substancia_atc_w),1,499)
	into STRICT	ds_estrutura_w
	;
	if (coalesce(ds_retorno_w::text, '') = '') or (length(ds_retorno_w) < 4000) then
		ds_retorno_w	:= substr(ds_retorno_w ||ds_estrutura_w ||chr(13) || chr(10),1,4000);
	end if;
	end;
END LOOP;
CLOSE C01;

return ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
