CREATE OR REPLACE FUNCTION obter_estrutura_interna_mat ( nr_seq_tipo_p bigint, cd_empresa_p bigint, cd_estabelecimento_p bigint, cd_perfil_p bigint, ie_opcao_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_opcao_p
0 - identifica se liberado uso
1 - identifica se liberaco visualização cadastro
*/
ie_liberado_w	varchar(1) := 'N';
ie_retorno_w	varchar(1);
ie_opcao_w	smallint := 0;
nm_usuario_w	usuario.nm_usuario%type;


c01 CURSOR FOR
SELECT	coalesce(CASE WHEN ie_opcao_w=1 THEN  coalesce(ie_permite_visualizar,'S')  ELSE ie_permite END ,'N')
from	mat_util_estrutura
where	coalesce(cd_empresa,coalesce(cd_empresa_p,0))			= coalesce(cd_empresa_p,0)
and	coalesce(cd_estabelecimento,coalesce(cd_estabelecimento_p,0))	= coalesce(cd_estabelecimento_p,0)
and	coalesce(cd_perfil, coalesce(cd_perfil_p,0))			= coalesce(cd_perfil_p,0)
and	coalesce(nm_usuario_lib, nm_usuario_w)			= nm_usuario_w
and	nr_seq_tipo					= nr_seq_tipo_p
order by	nm_usuario_lib desc,
	cd_perfil desc,
	cd_estabelecimento desc,
	cd_empresa desc;


BEGIN

nm_usuario_w	:= coalesce(nm_usuario_p,'X');

if (nm_usuario_w = 'X') then
	nm_usuario_w	:= coalesce(obter_usuario_ativo,'X');
end if;

if (coalesce(ie_opcao_p,0) > 0) then
	ie_opcao_w	:= ie_opcao_p;
end if;

open c01;
loop
fetch c01 into
	ie_liberado_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	ie_retorno_w	:= ie_liberado_w;
end loop;
close c01;

return ie_liberado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
