CREATE OR REPLACE FUNCTION obter_estrutura_interna_proc ( nr_seq_tipo_p bigint, cd_empresa_p bigint, cd_estabelecimento_p bigint, cd_perfil_p bigint, cd_funcao_ativa_p bigint, nm_usuario_p text, nr_seq_order_unit_p bigint default null) RETURNS varchar AS $body$
DECLARE


ie_liberado_w					varchar(1) 		:= 'N';
ie_retorno_w					varchar(1);
nr_sequencia_w					pi_util_estrutura.nr_sequencia%type;

c01 CURSOR FOR
SELECT	coalesce(ie_permite,'N'), nr_sequencia
from	pi_util_estrutura
where	coalesce(cd_empresa,coalesce(cd_empresa_p,0))			= coalesce(cd_empresa_p,0)
and	coalesce(cd_estabelecimento,coalesce(cd_estabelecimento_p,0))	= coalesce(cd_estabelecimento_p,0)
and	coalesce(cd_perfil, coalesce(cd_perfil_p,0))				= coalesce(cd_perfil_p,0)
and	nr_Seq_tipo							= nr_seq_tipo_p
and (coalesce(cd_setor_atendimento::text, '') = '' or cd_setor_atendimento		= wheb_usuario_pck.get_cd_setor_atendimento)
and (coalesce(nm_usuario_lib::text, '') = '' or nm_usuario_lib			= nm_usuario_p)
and	coalesce(cd_funcao,coalesce(cd_funcao_ativa_p,0))			= coalesce(cd_funcao_ativa_p,0)
order by coalesce(cd_empresa,0),
	 coalesce(cd_estabelecimento,0),
	 coalesce(cd_perfil,0),
	 coalesce(cd_funcao,0);

BEGIN

if (obter_estrut_int_order_type(nr_seq_tipo_p, null, nr_seq_order_unit_p) = 'N') then
	return 'N';
end if;


open c01;
loop
fetch c01 into
	ie_liberado_w,
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	ie_retorno_w	:= ie_liberado_w;
end loop;
close c01;

return ie_liberado_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
