SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_estrutura_material ( cd_material_p bigint, ie_estrutura_p text) RETURNS bigint AS $body$
DECLARE


/* 	ie_estrutura_p
	C = Classe do material
	S = SubGrupo do material
	G = Grupo do material
*/
cd_classe_w		integer;
cd_subgrupo_w		smallint;
cd_grupo_w		smallint;
cont_w			smallint;
ds_resultado_w		integer;


BEGIN

begin
select	1
into STRICT	cont_w
from	material
where	cd_material	= cd_material_p;

select	cd_classe_material
into STRICT	cd_classe_w
from	material
where	cd_material = cd_material_p;

if (ie_estrutura_p = 'C') then
	ds_resultado_w	:= cd_classe_w;
elsif (ie_estrutura_p = 'S') then
	begin
	select	cd_subgrupo_material
	into STRICT	cd_subgrupo_w
	from	classe_material
	where	cd_classe_material = cd_classe_w;

	ds_resultado_w	:= cd_subgrupo_w;
	end;
elsif (ie_estrutura_p = 'G') then
	begin
	select	cd_subgrupo_material
	into STRICT	cd_subgrupo_w
	from	classe_material
	where	cd_classe_material = cd_classe_w;

	select	cd_grupo_material
	into STRICT	cd_grupo_w
	from	subgrupo_material
	where	cd_subgrupo_material = cd_subgrupo_w;

	ds_resultado_w	:= cd_grupo_w;
	end;
end if;
exception
when others then
	ds_resultado_w := null;
end;

RETURN ds_resultado_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
