CREATE OR REPLACE FUNCTION obter_estrutura_procedimento ( cd_procedimento_p bigint, ie_origem_proced_p bigint, ie_tipo_p bigint) RETURNS varchar AS $body$
DECLARE


cd_area_procedimento_w		integer;
ds_area_procedimento_w		varchar(40);
cd_especialidade_w		varchar(40);
ds_especialidade_w		varchar(240);
cd_grupo_proc_w			varchar(40);
ds_grupo_proc_w			varchar(240);
ds_retorno_w			varchar(255);


BEGIN

select	cd_area_procedimento,
	ds_area_procedimento,
	cd_especialidade,
	ds_especialidade,
	cd_grupo_proc,
	ds_grupo_proc
into STRICT	cd_area_procedimento_w,
	ds_area_procedimento_w,
	cd_especialidade_w,
	ds_especialidade_w,
	cd_grupo_proc_w,
	ds_grupo_proc_w
from	estrutura_procedimento_v
where	cd_procedimento		= cd_procedimento_p
and	ie_origem_proced	= ie_origem_proced_p;

ds_retorno_w	:= substr(cd_area_procedimento_w || ' - ' || ds_area_procedimento_w || ' - ' || cd_especialidade_w || ' - ' ||
			ds_especialidade_w  || ' - ' || cd_grupo_proc_w || ' - ' || ds_grupo_proc_w,1,255);

return	ds_retorno_w;

end;
$body$
