CREATE OR REPLACE FUNCTION obter_estrut_conta_conv (nr_seq_ret_item_p bigint, ie_emite_conta_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* 'D' para descricao da estrutura, 'C' para sequencia de apresentacao */

cd_convenio_w	integer;
ds_retorno_w	varchar(80);




BEGIN

select	max(a.cd_convenio)
into STRICT	cd_convenio_w
from	convenio_retorno_item b,
	convenio_retorno a
where	b.nr_seq_retorno = a.nr_sequencia
and	b.nr_sequencia = nr_seq_ret_item_p;

if (ie_opcao_p = 'D') then
	select	coalesce(max(ds_titulo),'0')
	into STRICT	ds_retorno_w
	from	convenio_estrutura_conta
	where	cd_estrutura = obter_somente_numero(ie_emite_conta_p)
	and	cd_convenio = cd_convenio_w;
else
	select	coalesce(max(nr_seq_conta),'0')
	into STRICT	ds_retorno_w
	from	convenio_estrutura_conta
	where	cd_estrutura = obter_somente_numero(ie_emite_conta_p)
	and	cd_convenio = cd_convenio_w;
end if;

if (ds_retorno_w = '0') then
	begin
	if (ie_opcao_p = 'D') then
		select	max(ds_titulo)
		into STRICT	ds_retorno_w
		from	convenio_estrutura_conta
		where	cd_estrutura = obter_somente_numero(ie_emite_conta_p)
		and	coalesce(cd_convenio::text, '') = '';
	else
		select	max(nr_seq_conta)
		into STRICT	ds_retorno_w
		from	convenio_estrutura_conta
		where	cd_estrutura = obter_somente_numero(ie_emite_conta_p)
		and	coalesce(cd_convenio::text, '') = '';
	end if;
	end;
end if;

return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
