CREATE OR REPLACE FUNCTION obter_estrut_int_order_type (nr_seq_tipo_p bigint, nr_seq_pi_estrutura_cad_p bigint, nr_seq_order_unit_p bigint) RETURNS varchar AS $body$
DECLARE



ie_retorno_w	varchar(1) := 'S';


BEGIN

if (nr_seq_tipo_p IS NOT NULL AND nr_seq_tipo_p::text <> '' AND nr_seq_order_unit_p IS NOT NULL AND nr_seq_order_unit_p::text <> '') then
	select 	coalesce(max('S'), 'N')
	into STRICT	ie_retorno_w
	from    pi_estrutura a,
		pi_estrutura_cad b,
		proc_order_type c,
		cpoe_order_unit d
	where	a.nr_seq_tipo = nr_seq_tipo_p
	and	a.nr_sequencia = b.nr_seq_estrutura
	and	((b.nr_sequencia = nr_seq_pi_estrutura_cad_p) or (coalesce(nr_seq_pi_estrutura_cad_p::text, '') = ''))
	and	c.nr_seq_proc_interno = b.nr_seq_proc_int
	and	c.nr_seq_order_type = d.nr_seq_cpoe_tipo_pedido
	and d.nr_sequencia = nr_seq_order_unit_p;

end if;

return ie_retorno_w;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
