SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_estrut_princ_ativo ( nr_seq_ficha_tecnica_p bigint, ie_opcao_p text default 'T') RETURNS varchar AS $body$
DECLARE


/* ie_opcao_p:
P = Somente o Principio ativo passado no parâmetro nr_seq_ficha_tecinca_p
D = Somente os Derivados Correspondentes do principio ativo passado no parâmetro
T = Todos
*/
ds_substancia_w		varchar(80);
ds_retorno_w		varchar(4000);

c01 CURSOR FOR
	SELECT	ds_substancia
	from	medic_ficha_tecnica
	where	nr_seq_superior		= nr_seq_ficha_tecnica_p;


BEGIN

if (ie_opcao_p  ('T','P')) then
	/* Pega o nome da molécula principal */

	select	ds_substancia
	into STRICT	ds_retorno_w
	from	medic_ficha_tecnica
	where 	nr_sequencia	= nr_seq_ficha_tecnica_p;
end if;

if (ie_opcao_p in ('T','D')) then
	/* Pega os derivados correspondentes */

	open c01;
	loop
	fetch c01 into
		ds_substancia_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		if (coalesce(ds_retorno_w::text, '') = '') then
			ds_retorno_w	:= ds_substancia_w;
		else
			ds_retorno_w	:= ds_retorno_w || ', ' || ds_substancia_w;
		end if;
		end;
	end loop;
	close c01;
end if;

return	substr(ds_retorno_w,1,2000);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
