CREATE OR REPLACE FUNCTION obter_estrut_schem_funcao ( nr_seq_objeto_p bigint) RETURNS varchar AS $body$
DECLARE

			
ds_lista_obj_w		varchar(4000) := nr_seq_objeto_p;
nr_seq_obj_sup_w	objeto_schematic.nr_seq_obj_sup%type := nr_seq_objeto_p;
ie_tipo_componente_w	objeto_schematic.ie_tipo_componente%TYPE;
nr_seq_funcao_sch_w	objeto_schematic.nr_seq_funcao_schematic%type;


BEGIN

while (nr_seq_obj_sup_w IS NOT NULL AND nr_seq_obj_sup_w::text <> '') loop 
	begin
	select	nr_seq_obj_sup,
		nr_seq_funcao_schematic, 
		ie_tipo_componente
	into STRICT	nr_seq_obj_sup_w,
		nr_seq_funcao_sch_w,
		ie_tipo_componente_w
	from	objeto_schematic     
	where	nr_sequencia = nr_seq_obj_sup_w;

	IF ie_tipo_componente_w = 'WDLG' THEN
		ds_lista_obj_w := to_char(coalesce(nr_seq_obj_sup_w,nr_seq_funcao_sch_w)) || ',' || to_char(-2) || ',' || ds_lista_obj_w;
		else
			ds_lista_obj_w := to_char(coalesce(nr_seq_obj_sup_w,nr_seq_funcao_sch_w)) || ',' || ds_lista_obj_w;
		END IF;
	
	end;
end loop;

return  ds_lista_obj_w;

end;
