CREATE OR REPLACE FUNCTION obter_etapas_adep_dp ( nr_prescricao_p bigint, nr_seq_solucao_p bigint, nm_usuario_p text, cd_perfil_p bigint) RETURNS bigint AS $body$
DECLARE


nr_etapas_w			bigint := 0;
cd_estabelecimento_w		smallint;
ie_utiliza_drenagem_w		varchar(1);


BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_solucao_p IS NOT NULL AND nr_seq_solucao_p::text <> '') then

	select	coalesce(max(cd_estabelecimento),1)
	into STRICT	cd_estabelecimento_w
	from	prescr_medica
	where	nr_prescricao	= nr_prescricao_p;

	ie_utiliza_drenagem_w := Obter_Param_Usuario(1113, 503, cd_perfil_p, nm_usuario_p, cd_estabelecimento_w, ie_utiliza_drenagem_w);

	select	count(*)
	into STRICT	nr_etapas_w
	from	hd_prescricao_evento
	where	nr_prescricao		= nr_prescricao_p
	and	nr_seq_solucao		= nr_seq_solucao_p
	and	((ie_evento		= 'DT') or
		 (ie_evento		= 'IT' AND ie_utiliza_drenagem_w = 'N') or (ie_evento		= 'SE'));

end if;

return nr_etapas_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
